// ie8 >

$view-ie7 : true;

%clearfix {
  &:before,
  &:after {
    content: "";
    display: table;
  }
  &:after {
    clear: both;
  }
  @if $view-ie7 {
    & {
      *zoom: 1;
    }
  }
}

