// sass template

{project}
│
├ css
│　└ {project}.css
└ sass
　　├ {project}.scss
　　│
　　├ _conf.scss
　　│
　　└ parts
　　　 ├ lib
　　　 │ ├ _reset.scss
　　　 │ └ _utility.scss
　　　 ├ _base.scss
　　　 ├ _layout.scss
　　　 └ _module.scss



// {project}.scss

@use "conf";
@use "parts/lib/reset";
@use "parts/lib/utility";
@use "parts/base";
@use "parts/layout";
@use "parts/module";


// {project}.scss の中にlib,mod内のscssファイルの内容を読み込む
// 名前がアンダーバーで始まるパーシャルファイルは、sassフォルダごとコンパイルしても個別に出力しない
// @use文の値で、パーシャルファイルの最初のアンダーバーと拡張子は省略可能

.foo {

  // A
  $body_font_size: 12px;
  $body_line_height: 1em;

  // B
  body {
    font-size: $body_font_size; // 12px
    line-height: $body_line_height; // 1em
  }
}


// Aが別ファイルのパーシャルなどからimportしたものの場合、上記のコードならBは問題無くcssに反映される
// しかし、Aのパーシャル内で仮に変更があった下記のような場合、

.foo {

  // A'
  $body_font_size: 14px; // 値が更新されていた
  $text_line_height: 1.5em; // 変数名と値が更新されていた

  // B
  body {
    font-size: $body_font_size; // 14px
    line-height: $body_line_height; // コンパイルエラー
  }
}


// Bの $body_font_size には問題なくA'の値が適用されるが、$body_line_height は定義されていないのでエラーになる
// そこで、下記のようにBで!defaultを指定しておくことにより、


.foo {

  // A'
  $body_font_size: 14px; // 値が更新されていた
  $text_line_height: 1.5em; // 変数名と値が更新されていた

  // B
  $body_font_size: 12px !default;
  $body_line_height: 1em !default;
  body {
    font-size: $body_font_size; // 14px
    line-height: $body_line_height; // 1em
  }
}


// A'の変数宣言はBにとって問題が無く、値が更新された $body_font_size には A'の値が適用され、A'の変数宣言に問題がある $body_line_height には !default の値が適用される

