// includeした場所にmixinの内容を読み込む
// includeした場所よりmixinの表記場所が下にあるとコンパイルエラー
// mixinの中でincludeすることも可
// mixinの中でincludeする場合は互いのmixinの表記順にルールは無い
// 使えないmixin名: 数字から始まる,@などの記号,--(ハイフン2個)から始まる

// scss

@mixin baz($value: 10px) {  // : 10px = 初期値
  margin: 0;
  padding: $value;
}

.foo {
  @include baz;
  color: #000;
}

.bar{
  @include baz(20px);
  color: #f00;
}


