// css

.foo {
  margin: 0;
  padding: 10px;  // 引数を指定しなければ初期値が反映される
  color: #000;
}

.bar {
  margin: 0;
  padding: 20px;
  color: #f00;
}

// 初期値は未設定でも可 @mixin baz($value) { 〜 }
// 初期値が未設定の時、includeで引数を設定しなければエラー
// 引数の複数設定可 @mixin baz($value1: 10px, $value2: 20px, 〜 ) { 〜 }

@mixin foo1($value) {
  text-shadow: $value;
}

.bar1 {
  @include foo1(1px 1px 0 #555, -1px -1px 0 #eee); // 1つの引数に2つの値を渡しているのでエラー
  @include foo1((1px 1px 0 #555, -1px -1px 0 #eee)); // valid
  @include foo1(unquote("1px 1px 0 #555, -1px -1px 0 #eee")); // valid
}


@mixin foo2($value...) { // 引数の数を限定しない
  text-shadow: $value;
}

.bar2 {
  @include foo2(1px 1px 0 #555, -1px -1px 0 #eee); // valid
}



