@mixin foo($t, $r, $b, $l) {
  padding-top: $t;
  padding-right: $r;
  padding-bottom: $b;
  padding-left: $l;
}

.bar {
  $value: 10px, 20px, 30px, 40px;

  @include foo($value); // $t に4つの値を渡し、$r,$b,$l に値が入らないのでエラー
  @include foo($value...); // valid
}

// scss

@mixin media($value) {

  @if $value == pc {
    @media(min-width:769px){
      @content;
    }
  }
  @if $value == tablet {
    @media(max-width:768px) and (min-width: 321px){
      @content;
    }
  }
  @if $value == sp {
    @media(max-width:320px){
      @content;
    }
  }
}

#header {

  background: #edf;
  color: #222;

  @include media(pc) {
    height: 100px;
    font-size: 18px;
  }
  @include media(tablet) {
    height: 60px;
    font-size: 16px;
  }
  @include media(sp) {
    height: 30px;
    font-size: 12px;
  }
}


