// css

.foo, .bar {
  margin: 0;
  padding: 10px;
}

.foo {
  color: #000;
}

.bar {
  color: #f00;
}

// extendした場所のセレクタ(.fooと.bar)が、指定したセレクタ(%baz)に代わって表記される
// mixinと違い、指定したセレクタの場所がextendした場所より上になければエラーというルールは無い
// 指定したセレクタが無い場合はエラーになるが、@extend %baz !optional; とすれば回避する
// @mediaの中から、外にあるプレースホルダセレクタはextendできない

// sass

.foo_1 {
  content: "1";

  .foo_2a {
    content: "2a";
  }
  @at-root .foo_2b { /* セレクタの前に付けることで親(.foo_1)の外に出す */
    content: "2b";
  }
}

.bar_1 {
  content: "1";

  @at-root { /* 複数のセレクタを親(.bar_1)の外に出す */
    .bar_2 {
      content: "2";
    }
    .bar_3 {
      content: "3";
    }
  }
}

@media all and (orientation:landscape) {
  .baz_1 {
    content: "1";

    @at-root .baz_2a { /* @mediaの外に出ない */
      content: "2a";
    }
    @at-root (without: media) {
      .baz_2b { /* @mediaの外に出る */
        content: "2b";
      }
    }
    @at-root (without: media rule) {
      .baz_2c { /* @mediaの外に出て親(.baz_1)からも出る */
        content: "2c";
      }
    }
    @at-root (with: media) {
      .baz_2d { /* @mediaの外に同じ@mediaを作り、親(.baz_1)からは出る */
        content: "2d";
      }
    }
  }
}


