// css


.foo_1 {
  content: "1";
}
.foo_1 .foo_2a {
  content: "2a";
}
.foo_2b {
  /* セレクタの前に付けることで親(.foo_1)の外に出す */
  content: "2b";
}


.bar_1 {
  content: "1";
}
/* 複数のセレクタを親(.bar_1)の外に出す */
.bar_2 {
  content: "2";
}
.bar_3 {
  content: "3";
}


@media all and (orientation: landscape) {
  .baz_1 {
  content: "1";
  }
  .baz_2a {
  /* @mediaの外に出ない */
  content: "2a";
  }
}
.baz_1 .baz_2b {
  /* @mediaの外に出る */
  content: "2b";
}
.baz_2c {
  /* @mediaの外に出て親(.baz_1)からも出る */
  content: "2c";
}
@media all and (orientation: landscape) {
  .baz_2d {
  /* @mediaの外に同じ@mediaを作り、親(.baz_1)からは出る */
  content: "2d";
  }
}

