// Gruntfile.js

module.exports = function(grunt) {

  grunt.initConfig({

    // for valiable
    pkg: grunt.file.readJSON('package.json'),


    // js: validate
    jshint: {
      beforeconcat: 'jsmod/mod/**/*.js'
    },

    // js: concat
    concat: {
      dist:
        src: [
          'jsmod/lib/**/zepto-*.js',
          'jsmod/lib/**/*.js',
          '!jsmod/lib/**/jquery-*.js'
          'jsmod/src/**/*.js'
        ],
        dest: 'js/.js'
      }
    },

    // js: minify
    uglify: {
      dist: {
        src: '',
        dest: 'js/.js'
      }
    },

    // compass config
    compass: {
      test: {
        options: {
          config: 'config.rb'
        }
      },
      release: {
        options: {
          config: 'config.rb',
          environment: 'production'
        }
      }
    },

  });


  require('load-grunt-tasks')(grunt, {pattern: ['grunt-contrib-*', 'grunt-shell']});

  grunt.registerTask('default', ['jshint', 'concat', 'compass:test']);
  // test用タスク - sassコンパイルはexpanded
  grunt.registerTask('r', ['jshint', 'concat', 'uglify', 'compass:release']);
  // release用タスク - sassコンパイルはcompressed, jsとcssはminify

};


