console.log(arr1[2]);  // qux
console.log(arr2[2]);  // qux  arr2が参照する値も変わる(同じ実体値を参照している)




// ２）配列の実体コピー

function copyArray(inArr) {  // 配列を実体コピーする関数
  var outArr = [];  // 新しい配列を作る
  for (var i = 0; i < inArr.length; i++) {
    if (inArr[i] instanceof Array) {  // inArr[i] が Array(配列) なら true
      outArr[i] = copyArray(inArr[i]);  // trueならもう一度関数を呼ぶ
    } else {
      outArr[i] = inArr[i];  // trueでなければ実体の値を代入
    }
  }
  return outArr;
}

var arr1 = [['foo0', 'bar0'], ['foo1','bar1']];
var arr2 = copyArray(arr1);  // 実体の複製ができるため、

arr1[1][1] = 'qux1';  // arr1が参照する値を変えても、

console.log(arr1[1][1]);  // qux1
console.log(arr2[1][1]);  // bar1  arr2が参照する値は変化しない

