};
// コンストラクタ(オブジェクトの生成元)となる関数オブジェクトcnsを定義
// この場合のthisは、cnsを元に生成される新たなオブジェクト(インスタンス)を参照する

var ins = new cns(); // new演算子でcnsを呼ぶと、インスタンスが生成され、それが戻り値になる

console.log(ins.val); // foo // cnsをモデルとして、insのプロパティvalが定義された


console.log(cns); // function cns() // cns は関数
console.log(cns.prototype); // cns {} // cns.prototype は関数cnsの雛形オブジェクト
console.log(ins); // cns {val: 'foo'} // ins は生成されたインスタンスオブジェクト ins {val: 'foo'}
console.log(ins.__proto__); // cns {} // ins.__proto__ は関数cnsの雛形オブジェクト
console.log(ins.constructor); // function cns() // insのコンストラクタは関数cns
console.log(ins instanceof cns); // true // insはcnsのインスタンス
console.log(Object.getPrototypeOf(ins) === cns.prototype); // true // insはcnsのprototypeオブジェクトを参照可能


