console.log(ins.__proto__); // Object {constructor: function} // __proto__は、生成元(cns)の雛形
console.log(cns.prototype === ins.__proto__); // true // 上記の2つは同じもの
console.log(ins.prototype); // undefined // insは関数オブジェクトではないので雛形を持たない
console.log(cns.prototype.isPrototypeOf(ins)); // true // insのプロトタイプはcns.prototype


console.log(Object); // function Object() { [native code] } // 大元のObject関数
console.log(Object.prototype); // Object {...} // Object // Objectが持つ雛形
console.log(Object.prototype.__proto__); // null // 雛形に生成元は無い

console.log(ins.__proto__.__proto__); // Object {...} // Object // ins.__proto__ の生成元の雛形を参照
console.log(ins.__proto__.__proto__ === Object.prototype); // true // ins.__proto__のモデルはObjectが持つ雛形
console.log(ins.__proto__.__proto__.__proto__); // null // Objectに生成元は無い


