fnc1.prototype = obj1;  // fnc1のprototypeに、オブジェクトobj1を定義
var obj2 = new fnc1();  // fnc1をコンストラクタとして、インスタンスobj2を生成する

function fnc2() {};  // 関数オブジェクトを定義
fnc2.prototype = obj2;  // fnc2のprototypeに、オブジェクトobj2を定義
var obj3 = new fnc2();  // fnc2をコンストラクタとして、インスタンスobj3を生成する

console.log(obj3.val);  // 42

