// クロージャを使って3つずつ出力

<!doctype html>
<title>get_json_closer.html</title>
<meta charset="utf-8">

<h1>This page is get_json_closer.html</h1>
<h2>request: fruit_list.json</h2>
<button onclick="getJsonThreeFruits()">get json 3 fruits</button>

<table border="1">
<thead>
<tr>
<th>name</th>
<th>color</th>
</tr>
</thead>
<tbody>
</tbody>
</table>

<script>

// requestのsendと、trのappend
function sendReq(count) {
  var req = new XMLHttpRequest();
  req.open('GET', 'fruit_list.json');
  req.send();

  req.addEventListener('loadend', function() {
    var data = JSON.parse(req.responseText);

    for (i = 0; i < 3; i++) {
      var j = (count * 3) + i;  // 参照するデータの番号調整

      if(data.fruit_list[j]) {  // データが存在すれば実行
        var tbody = document.getElementsByTagName('tbody')[0];
        var tr  = document.createElement('tr');
        var name  = document.createElement('td');
        var color = document.createElement('td');
        name.appendChild(document.createTextNode(data.fruit_list[j].name));
        color.appendChild(document.createTextNode(data.fruit_list[j].color));
        tr.appendChild(name);
        tr.appendChild(color);
        tbody.appendChild(tr);
      }
    }
  });
}

// クロージャ
function getJson() {
  var count = 0;
  return function() {
    sendReq(count);
    count++;
  }
}

// インスタンス生成
var getJsonThreeFruits = new getJson();

</script>

