---

// main.js

import Vue from 'vue';
import SampleHeader from './component/header.vue';
import SampleContent from './component/content.vue';
import SampleFooter from './component/footer.vue';

Vue.config.productionTip = false;

new Vue({
  components: {
    SampleHeader,
    SampleContent,
    SampleFooter,
  },
}).$mount('#app');


